import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from 'chart.js';
import { Pie, Bar, Line } from 'react-chartjs-2';

ChartJS.register(
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
);

const Charts = ({ data }) => {
  if (!data) {
    return <div>Loading...</div>; // or a suitable fallback UI
  }

  const chartData = {
    labels: data.map(item => item.name),
    datasets: [
      {
        label: 'Sales Data',
        data: data.map(item => item.sales),
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4">Charts</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        <div className="bg-gray-800 p-4 rounded shadow">
          <h3 className="text-lg font-medium mb-2">Pie Chart</h3>
          <Pie data={chartData} />
        </div>
        <div className="bg-gray-800 p-4 rounded shadow">
          <h3 className="text-lg font-medium mb-2">Bar Chart</h3>
          <Bar data={chartData} />
        </div>
        <div className="bg-gray-800 p-4 rounded shadow">
          <h3 className="text-lg font-medium mb-2">Line Chart</h3>
          <Line data={chartData} />
        </div>
      </div>
    </div>
  );
};

export default Charts;
