import React, { useState } from 'react';

// Import any modal library if you're using one, e.g. from react-modal or similar
import Modal from 'react-modal';

const Table = ({ onDataChange }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;
  const [data, setData] = useState(Array.from({ length: 20 }, (_, index) => ({
    id: index + 1,
    name: `Product ${index + 1}`,
    price: (Math.random() * 100).toFixed(2),
    sales: Math.floor(Math.random() * 500),
  })));

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [newItem, setNewItem] = useState({ name: '', price: '', sales: '' });

  // Filter data based on the search term
  const filteredData = data.filter((item) =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedData = filteredData.slice(startIndex, startIndex + itemsPerPage);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const handleOpenModal = (item = null) => {
    setEditingItem(item);
    setNewItem(item ? { ...item } : { name: '', price: '', sales: '' });
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingItem(null);
  };

  const handleDeleteClick = (id) => {
    const newData = data.filter(item => item.id !== id);
    setData(newData);
    onDataChange(newData); // Notify the parent component about the data change
  };

  const handleSaveClick = () => {
    if (editingItem) {
      const updatedData = data.map(item => (item.id === editingItem.id ? { ...item, ...newItem } : item));
      setData(updatedData);
      onDataChange(updatedData); // Notify the parent component about the data change
    } else {
      const newData = [...data, { id: data.length + 1, ...newItem }];
      setData(newData);
      onDataChange(newData); // Notify the parent component about the data change
    }
    handleCloseModal();
  };

  return (
    <div id="table" className="p-4">
      <h2 className="text-xl mb-4">Data Table</h2>

      {/* Search input */}
      <input
        type="text"
        placeholder="Search Product..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="mb-4 p-2 border rounded w-full"
      />

      {/* Add/Edit button */}
      <button
        onClick={() => handleOpenModal()}
        className="mb-4 p-2 bg-green-500 text-white rounded"
      >
        Add Product
      </button>

      {/* Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
          <thead className="bg-gray-200">
            <tr>
              <th className="py-2 px-4 text-left text-sm font-medium text-gray-600">ID</th>
              <th className="py-2 px-4 text-left text-sm font-medium text-gray-600">Product Name</th>
              <th className="py-2 px-4 text-left text-sm font-medium text-gray-600">Price</th>
              <th className="py-2 px-4 text-left text-sm font-medium text-gray-600">Sales</th>
              <th className="py-2 px-4 text-left text-sm font-medium text-gray-600">Actions</th>
            </tr>
          </thead>
          <tbody>
            {paginatedData.map((item) => (
              <tr key={item.id} className="border-b">
                <td className="py-2 px-4 text-sm">{item.id}</td>
                <td className="py-2 px-4 text-sm">{item.name}</td>
                <td className="py-2 px-4 text-sm">${item.price}</td>
                <td className="py-2 px-4 text-sm">{item.sales}</td>
                <td className="py-2 px-4 text-sm">
                  <button
                    onClick={() => handleOpenModal(item)}
                    className="text-blue-500 hover:underline"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteClick(item.id)}
                    className="text-red-500 ml-2 hover:underline"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Pagination Controls */}
      <div className="flex justify-between mt-4">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
          className="p-2 bg-gray-300 rounded"
        >
          Previous
        </button>
        <span>Page {currentPage} of {totalPages}</span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
          className="p-2 bg-gray-300 rounded"
        >
          Next
        </button>
      </div>

      {/* Modal for Adding/Editing Product */}
      <Modal isOpen={isModalOpen} onRequestClose={handleCloseModal} className="modal" overlayClassName="overlay">
        <h2 className="text-xl mb-4">{editingItem ? 'Edit Product' : 'Add Product'}</h2>
        <input
          type="text"
          placeholder="Product Name"
          value={newItem.name}
          onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
          className="mb-2 p-2 border rounded w-full"
        />
        <input
          type="number"
          placeholder="Price"
          value={newItem.price}
          onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
          className="mb-2 p-2 border rounded w-full"
        />
        <input
          type="number"
          placeholder="Sales"
          value={newItem.sales}
          onChange={(e) => setNewItem({ ...newItem, sales: e.target.value })}
          className="mb-2 p-2 border rounded w-full"
        />
        <button onClick={handleSaveClick} className="p-2 bg-blue-500 text-white rounded">
          {editingItem ? 'Update' : 'Add'}
        </button>
        <button onClick={handleCloseModal} className="p-2 bg-gray-300 rounded ml-2">Cancel</button>
      </Modal>
    </div>
  );
};

export default Table;
