import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from 'chart.js';
import { Pie, Bar, Line } from 'react-chartjs-2';

// Register required components
ChartJS.register(
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
);

const Charts = () => {
  const chartData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Sales Data',
        data: [65, 59, 80, 81, 56, 55],
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  // Options for the Bar and Line charts
  const options = {
    responsive: true,
    maintainAspectRatio: false, // Allow resizing
    scales: {
      x: {
        beginAtZero: true,
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div id="charts" className="p-4 grid grid-cols-3 gap-4">
      {/* Bar Chart */}
      <div className="col-span-2" style={{ height: '250px' }}>
        <h2 className="text-xl mb-4">Bar Chart</h2>
        <Bar data={chartData} options={options} />
      </div>

      {/* Pie Chart */}
      <div className="col-span-1">
        <h2 className="text-xl mb-4">Pie Chart</h2>
        <Pie data={chartData} />
      </div>

      {/* Line Chart */}
      <div className="col-span-2" style={{ height: '250px' }}>
        <h2 className="text-xl mb-4">Line Chart</h2>
        <Line data={chartData} options={options} />
      </div>
    </div>
  );
};

export default Charts;
