import { Pie, Bar, Line } from 'react-chartjs-2';

const Charts = () => {
  const chartData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Sales Data',
        data: [65, 59, 80, 81, 56, 55],
        backgroundColor: ['rgba(255, 99, 132, 0.2)', 'rgba(54, 162, 235, 0.2)', /* more colors */],
        borderColor: ['rgba(255, 99, 132, 1)', 'rgba(54, 162, 235, 1)', /* more colors */],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div id="charts" className="p-4">
      <h2 className="text-xl mb-4">Charts</h2>
      <div className="grid grid-cols-2 gap-4">
        <Pie data={chartData} />
        <Bar data={chartData} />
        <Line data={chartData} />
        {/* Add Histogram chart here */}
      </div>
    </div>
  );
};
