import React, { useState } from 'react';
import './Table.css'; // Import the CSS file

const Table = ({ onDataChange }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;
  const [data, setData] = useState(Array.from({ length: 20 }, (_, index) => ({
    id: index + 1,
    name: `Product ${index + 1}`,
    price: (Math.random() * 100).toFixed(2),
    sales: Math.floor(Math.random() * 500),
  })));

  const [editingItem, setEditingItem] = useState(null);
  const [newItem, setNewItem] = useState({ name: '', price: '', sales: '' });
  const [isModalOpen, setModalOpen] = useState(false); // Modal state

  const filteredData = data.filter((item) =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedData = filteredData.slice(startIndex, startIndex + itemsPerPage);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const handleEditClick = (item) => {
    setEditingItem(item);
    setNewItem(item); // Set new item to the item being edited
    setModalOpen(true); // Open modal for editing
  };

  const handleDeleteClick = (id) => {
    const newData = data.filter(item => item.id !== id);
    setData(newData);
    onDataChange(newData);
  };

  const handleSaveClick = () => {
    if (editingItem) {
      const updatedData = data.map(item => (item.id === editingItem.id ? { ...item, ...newItem } : item));
      setData(updatedData);
      setEditingItem(null);
    } else {
      const newData = [...data, { id: data.length + 1, ...newItem }];
      setData(newData);
    }
    setNewItem({ name: '', price: '', sales: '' }); // Reset new item fields
    setModalOpen(false); // Close modal after saving
    onDataChange(data);
  };

  const handleAddClick = () => {
    setNewItem({ name: '', price: '', sales: '' }); // Reset for new entry
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  return (
    <div id="table" className="p-4">
      <h2 className="text-xl mb-4">Data Table</h2>

      <input
        type="text"
        placeholder="Search Product..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="mb-4 p-2 border rounded w-full"
      />

      <button onClick={handleAddClick} className="mb-4 p-2 bg-green-500 text-white rounded">
        Add New Product
      </button>

      {/* Modal for Add/Edit */}
      <Modal 
        isOpen={isModalOpen} 
        onClose={handleModalClose} 
        onSave={handleSaveClick}
        newItem={newItem} // Pass newItem state
        setNewItem={setNewItem} // Pass setNewItem function
      />

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white">
          <thead className="bg-gray-200">
            <tr>
              <th className="py-2 px-2 text-sm">ID</th>
              <th className="py-2 px-2 text-sm">Product Name</th>
              <th className="py-2 px-2 text-sm">Price</th>
              <th className="py-2 px-2 text-sm">Sales</th>
              <th className="py-2 px-2 text-sm">Actions</th>
            </tr>
          </thead>
          <tbody>
            {paginatedData.map((item) => (
              <tr key={item.id} className="border-b">
                <td className="py-2 px-2 text-sm">{item.id}</td>
                <td className="py-2 px-2 text-sm">{item.name}</td>
                <td className="py-2 px-2 text-sm">${item.price}</td>
                <td className="py-2 px-2 text-sm">{item.sales}</td>
                <td className="py-2 px-2 text-sm">
                  <button
                    onClick={() => handleEditClick(item)}
                    className="text-blue-500"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteClick(item.id)}
                    className="text-red-500 ml-2"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="flex justify-between mt-4">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
          className="p-2 bg-gray-300 rounded"
        >
          Previous
        </button>
        <span>Page {currentPage} of {totalPages}</span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
          className="p-2 bg-gray-300 rounded"
        >
          Next
        </button>
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, onSave, newItem, setNewItem }) => {
  if (!isOpen) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewItem((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="overlay fixed inset-0 flex items-center justify-center">
      <div className="modal">
        <h2 className="text-lg mb-4">{newItem.id ? 'Edit Product' : 'Add Product'}</h2>

        <div className="mb-4">
          <label className="block mb-1">Product Name</label>
          <input
            type="text"
            name="name"
            value={newItem.name}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block mb-1">Price</label>
          <input
            type="number"
            name="price"
            value={newItem.price}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block mb-1">Sales</label>
          <input
            type="number"
            name="sales"
            value={newItem.sales}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>

        <div className="flex justify-between">
          <button onClick={onSave} className="p-2 bg-blue-500 text-white rounded">
            Save
          </button>
          <button onClick={onClose} className="p-2 bg-gray-300 rounded">
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default Table;
