import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from 'chart.js';
import { Pie, Bar, Line } from 'react-chartjs-2';

ChartJS.register(
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
);

const Charts = ({ data, darkMode }) => {
  const backgroundColors = darkMode
    ? ['rgba(44, 62, 80, 0.7)', 'rgba(52, 152, 219, 0.7)', 'rgba(231, 76, 60, 0.7)', 'rgba(46, 204, 113, 0.7)', 'rgba(155, 89, 182, 0.7)', 'rgba(241, 196, 15, 0.7)']
    : ['rgba(255, 99, 132, 0.2)', 'rgba(54, 162, 235, 0.2)', 'rgba(255, 206, 86, 0.2)', 'rgba(75, 192, 192, 0.2)', 'rgba(153, 102, 255, 0.2)', 'rgba(255, 159, 64, 0.2)'];

  const chartData = {
    labels: data.map(item => item.name),
    datasets: [
      {
        label: 'Sales Data',
        data: data.map(item => item.sales),
        backgroundColor: backgroundColors,
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div id="charts" className={`p-4 grid grid-cols-3 gap-4 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
      <div className="col-span-2">
        <h2 className={`text-xl mb-4 ${darkMode ? 'text-white' : 'text-black'}`}>Bar Chart</h2>
        <Bar data={chartData} />
      </div>

      <div className="col-span-1">
        <h2 className={`text-xl mb-4 ${darkMode ? 'text-white' : 'text-black'}`}>Pie Chart</h2>
        <Pie data={chartData} />
      </div>

      <div className="col-span-2">
        <h2 className={`text-xl mb-4 ${darkMode ? 'text-white' : 'text-black'}`}>Line Chart</h2>
        <Line data={chartData} />
      </div>
    </div>
  );
};

export default Charts;
