import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from 'chart.js';
import { Pie, Bar, Line } from 'react-chartjs-2';

ChartJS.register(
  ArcElement,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
);

const Charts = ({ data }) => {
  // Prevents accessing 'map' on undefined
  if (!data) {
    return <div>Loading...</div>; // or a suitable fallback UI
  }

  // Prepare the data for charts based on the passed data
  const chartData = {
    labels: data.map(item => item.name),
    datasets: [
      {
        label: 'Sales Data',
        data: data.map(item => item.sales),
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div id="charts" className="p-4 grid grid-cols-3 gap-4">
      <div className="col-span-2">
        <h2 className="text-xl mb-4">Bar Chart</h2>
        <Bar data={chartData} />
      </div>
      <div className="col-span-1">
        <h2 className="text-xl mb-4">Pie Chart</h2>
        <Pie data={chartData} />
      </div>
      <div className="col-span-2">
        <h2 className="text-xl mb-4">Line Chart</h2>
        <Line data={chartData} />
      </div>
    </div>
  );
};

export default Charts;
